// test/joke-api.test.ts
import { JokeAPI, Joke } from '../src/api/joke-api';

describe('JokeAPI', () => {
  it('should retrieve a random joke', async () => {
    const api = new JokeAPI();

    const joke: Joke = await api.getRandomJoke();

    // Write assertions here to test the retrieved joke
    expect(joke).toHaveProperty('category');
    expect(joke).toHaveProperty('type');
    expect(joke).toHaveProperty('setup');
    expect(joke).toHaveProperty('delivery');
  });
});
